		'pk_test_51Hc2wBBczRsScN8t8gym19lhzl9GPnEsqlBqxWoC6YdrxTdTVj1fMimzJN4imuDWC8PjhWOPEuc4LpcCH3TrU1P800utMyo7oV';

	const onToken = (token) => {
		axios({
			url: 'payment',
			method: 'post',
			data: {
				amount: priceForStripe,
				token
